import React, { useState, useEffect, MouseEvent, FocusEvent } from "react";
import TextField from "@mui/material/TextField";
import { InputAdornment, Tooltip } from "@mui/material";
import SendIcon from "@mui/icons-material/Send";
import { createTheme, ThemeProvider } from "@mui/material/styles";
import { blue } from "@mui/material/colors";
import Box from "@mui/material/Box";
import Container from "@mui/material/Container";
import { useMediaQuery } from "@mui/material";
import { Theme } from "@mui/system";
import { useTheme } from "@mui/system";

const theme = createTheme({
  components: {
    MuiInputLabel: {
      styleOverrides: {
        root: {
          color: "white",
        },
      },
    },
    MuiOutlinedInput: {
      styleOverrides: {
        input: {
          color: "white",
        },
        notchedOutline: {
          "&:hover": {
            borderColor: blue[500],
          },
          "&.Mui-focused": {
            borderColor: blue[500],
          },
          borderColor: blue[500],
        },
      },
    },
  },
});

interface Message {
  text: string;
  isUser: boolean;
}

interface MyTextFieldProps {
  onSubmit: (inputValue: string) => void;
  messages: Message[];
}


const MyTextField: React.FC<MyTextFieldProps> = ({ onSubmit, messages }) => {
  const [inputValue, setInputValue] = useState("");

  const handleInputChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setInputValue(event.target.value);
  };

  const handleSubmit = async (
    event?: MouseEvent<SVGSVGElement> | React.FormEvent<HTMLFormElement>
  ) => {
    event?.preventDefault();
    onSubmit(inputValue);
    setInputValue("");
  };

  const handleKeyPress = (event: React.KeyboardEvent<HTMLDivElement>) => {
    if (event.key === "Enter") {
      event.preventDefault();
      handleSubmit();
    }
  };

  const handleBlur = (
    event: FocusEvent<HTMLInputElement | HTMLTextAreaElement>
  ) => {
    event.preventDefault();
  };

  return (
    <Box
      style={{
        display: "flex",
        justifyContent: "center",
        paddingBottom: "15px",
      }}
    >
      <Container maxWidth="md">
        <form onSubmit={handleSubmit}>
          <ThemeProvider theme={theme}>
            <TextField
              id="textField"
              variant="outlined"
              value={inputValue}
              label="Send a message..."
              onChange={handleInputChange}
              onKeyPress={handleKeyPress}
              multiline
              maxRows={1}
              fullWidth
              autoFocus={true}
              onBlur={handleBlur}
              sx={{
                "& .MuiOutlinedInput-root:hover .MuiOutlinedInput-notchedOutline":
                  {
                    borderColor: blue[500],
                  },
              }}
              InputLabelProps={{
                style: {
                  fontFamily: "Noto Sans, monospace", // Set the font family
                  fontSize: "16px", // Set the font size
                },
              }}
              InputProps={{
                style: {
                  fontFamily: "Noto Sans, monospace", // Set the font family
                  fontSize: "16px", // Set the font size
                  textAlign: "left",
                },

                endAdornment: (
                  <InputAdornment position="end">
                    <Tooltip title="Send it!">
                      <SendIcon
                        sx={{
                          color: blue[500],
                          cursor: "pointer",
                          "&:hover": {
                            cursor: "pointer",
                          },
                        }}
                        onClick={handleSubmit}
                      />
                    </Tooltip>
                  </InputAdornment>
                ),
              }}
            />
          </ThemeProvider>
        </form>
      </Container>
    </Box>
  );
};

export default MyTextField;
